package com.syntax.class06;

public class SwitchCasePractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char choice='N';
		String name;
		switch(choice) {
		case 'Y':
			name="Yes";
			break;
		case 'M':
			name="Maybe";
			break;
		case 'N':
			name="No";
			break;
		default:
			name="invalid";
			break;
			
		}

	}

}
