public class WhileloopEXP {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		while (a<10) {
			System.out.println("your name");
			a++;
		}
		System.out.println("== Anthor Example======");
		// i want to print numbers from 1 to 10
		int num=1;
		while (num<=10) {
			num++;
			System.out.print(num+" ");
			num++;
		}
		
		System.out.println("===== anthero example===");
		// i want to print numbers from 1 to 100
		int num2=1;
		while (num2<=100) {
			System.out.print(num2+" ");
			num2++;
		}
		
		System.out.println("=== anthor example====");
		int num3=10;
		while (num3<=100) {
			System.out.print(num3+" ");
			num3++;
		}
		System.out.println("=====================");
		// print numbers from 10 to 1
		int num4=10;
		while (num4>=1) {
			System.out.print(num4+" ");
			num4--;
		}
		System.out.println("====== anthor example =======");
		// prints numbers from 100 to 50
		int num5=100;
		while (num5>=50) {
			System.out.print(num5+" ");
			num5--;
		}
		System.out.println("====anthor example =====");
		// i want to print only Even number from 1 to 20
		int num6=2;
		while (num6<=20) {
			System.out.print(num6+" ");
			num6+=2;
		}
		System.out.println("====== anthore example ======");
		// i want to print the Even number only from 1 to 30
		int num7=2;
		while (num7<=30) {
			System.out.print(num7+" ");
			num+=2;
		}
	}

}
